transforms.result:
div.color-pallets{
  display: flex;
  gap: 5px;
  overflow: scroll;
  div{
    width: 50px;
    height: 50px;
  }
  & > div:nth-child(1){
    background-color: $black_color;
  }
  & > div:nth-child(2){
    background-color: $black_color_1;
  }
  & > div:nth-child(3){
    background-color: $black_color_2;
  }
  & > div:nth-child(4){
    background-color: $black_color_3;
  }
  & > div:nth-child(5){
    background-color: $black_color_4;
  }
  & > div:nth-child(6){
    background-color: $black_color_5;
  }
  & > div:nth-child(7){
    background-color: $black_color_6;
  }
  & > div:nth-child(8){
    background-color: $black_color_7;
  }
  & > div:nth-child(9){
    background-color: $black_color_8;
  }
  & > div:nth-child(10){
    background-color: $black_color_9;
  }
}
.box {
  padding: 30px;
  display: flex;
  flex-direction: column;
  gap: 20px;
}

.responsive-test {
  & > div {
    font-size: 1rem;
    width: 10rem;
    height: 10rem;
    color: $white_color_1;
    background-color: $main_color_2;
  }
}
.button-type-1{
   button{
     display: none;
   }
}
