transforms.result:
@import "./utilities/colors";
@import "./utilities/configs";
@import "./utilities/animations";
@import "./utilities/responsive";
@import "./utilities/fonts";


html{
  font-size: 14px;
  background-color: $html_background_color;
}
body{
  font-family: $font-family;
  background-color: $body_background_color;
  .container{
    overflow: hidden;
    //margin-top: 2.5rem;
    background-color: $main_background_color;
    min-width: 95vw;
    min-height: 95vh;
  }
}

*{
  margin: 0;
  padding: 0;
}

a {
  transition-duration: $default-transition-duration;
  color: $link-color;
  font-size: 1.1rem;
  user-select: none;
  text-decoration: none;
  white-space: nowrap;
  &:hover{
    color: lighten($link-color, 10%);
    opacity: .8;
  }
  &.decoration{
    text-decoration: underline;
  }
}

a.type_1 {
  color: $header_font_color;
  border: 3px solid transparent;
  width: 100%;
  text-align: center;
  padding: .5rem;
  &.active,
  &:hover
  {
    color: $main_color;
    border-bottom: 3px solid $main_color;
  }

  &:hover {
    background-color: lighten($white_color_1, 3%);
  }
}

// Auto complete styles
input:-webkit-autofill,
input:-webkit-autofill:hover,
input:-webkit-autofill:focus,
input:-webkit-autofill:active {
  transition: background-color 5000s ease-in-out 0s;
  -webkit-text-fill-color: $black_color_4;
  -webkit-box-shadow: 0 0 0px 1000px lighten($white_color_1, 3%) inset;
}

